import React, { useState } from 'react';
import Icon from 'react-native-vector-icons/Ionicons';
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  Pressable,
  Alert,
  ToastAndroid,
  Modal,TouchableOpacity
} from 'react-native';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import { color } from 'react-native-reanimated';
const Home = ({navigation}) => {

  const [name, SetName] = useState('');
  const [submitted, SetSubmitted] = useState(false);
  const [showWarning, SetshowWarning] = useState(false);
  const onPressHandler = () => {
    if (name.length > 4) {
      SetSubmitted(!submitted);
    } else {
      SetshowWarning(false);
    }
  }
  const onPressHandler1 = () => {

      SetshowWarning(true);

  }

  const onPressHandler2= () => {
    if (name.length > 2){
       create();
    }
    else{
      console.log('Group Name must be greater than 2 alphabets');
    }
  }

  return (
    <View style={styles.body}>
      <Modal
        visible={showWarning}
        transparent
        onRequestClose={() =>
          SetshowWarning(false)
        }
        animationType='fade'
        hardwareAccelerated
      >
        <View style={styles.centered_view}>
          <View style={styles.warning_modal}>
            <View style={styles.warning_title}>
              <Text style={styles.text}>Group Name</Text>
            </View>
            <View style={styles.warning_body}>


      <View style={styles.action}>
      <Text>{"   "}</Text>
                <FontAwesome 
                    name="users"

                    size={20}
                />
                <TextInput 
                    placeholder=" Enter Group name"

                    placeholderTextColor="#666666"
                    style={styles.textInput }


        onChangeText={(value) => SetName(value)}
                />

            </View>
            </View>

            <Pressable
              onPress={() => SetshowWarning(false)}
              style={styles.warning_button}
              android_ripple={{color:'#fff'}}
            >
              <Text style={styles.text} >OK</Text>
            </Pressable>

          </View>
        </View>
      </Modal>


        <TouchableOpacity onPress={onPressHandler1}

               style={styles.appButtonContainer}>
           <Icon  style={styles.appButtonText} name="add-circle" size={40} backgroundColor="#5B79E0" color='black' 


              >{" "}CREATE</Icon>
     </TouchableOpacity>
     <TouchableOpacity
                    onPress={() => navigation.navigate('FriendsList')}
                    style={[styles.signIn, {
                        borderColor: '#fff',
                        borderWidth: 1,
                        marginTop: 15
                    }]}
                >
                    <Text style={[styles.textSign, {
                        color: '#000'
                    }]}>Friends</Text>
                </TouchableOpacity>
                <TouchableOpacity
                    onPress={() => navigation.navigate('FriendsList')}
                    style={[styles.signIn, {
                        borderColor: '#fff',
                        borderWidth: 1,
                        marginTop: 15
                    }]}
                >
                    <Text style={[styles.textSign, {
                        color: '#000'
                    }]}>Family</Text>
                </TouchableOpacity>

     {
        submitted ?
          <Text style={styles.text}>
            You are registered as {name}
          </Text>
          :
          null
      }
    </View >
  );
};

const styles = StyleSheet.create({
  body: {
    flex: 1,
    backgroundColor: '#e6e6e6',
    alignItems: 'center',
  },
  text: {
    color: '#fff',
    fontSize: 20,
    margin: 10,
    textAlign: 'center',
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,

  },

  button: {
    width: 150,
    height: 50,
    alignItems: 'center',
  },
  centered_view: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#00000099'
  },
  warning_modal: {
    width: 300,
    height: 298,
    backgroundColor: '#e6e6e6',
    borderWidth: 1,
    borderColor: 'lightblue',
    borderRadius: 20,
  },
  warning_title: {
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightblue',
    borderTopRightRadius: 20,
    borderTopLeftRadius: 20,
  },
  warning_body: {
    height: 200,
    justifyContent: 'center',
    alignItems: 'center',
  },
  warning_button:{
    backgroundColor:'lightblue',
    borderBottomLeftRadius:20,
    borderBottomRightRadius:20,

  },
  action: {
    flexDirection: 'row',
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#f2f2f2',
    paddingBottom: 5
},
actionError: {
    flexDirection: 'row',
    marginTop: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#FF0000',
    paddingBottom: 5
},
textInput: {
    flex: 1,
    marginTop: Platform.OS === 'ios' ? 0 : -12,
    paddingLeft: 10,
    color: '#05375a',
},
appButtonContainer: {
  elevation: 8,
  backgroundColor: "#5B79E0",
  borderRadius: 10,
  paddingVertical: 12,
  paddingHorizontal: 12,
  marginLeft:'55%',
  marginTop:15,
},
appButtonText: {
  fontSize: 22,
  color: "#fff",
  fontWeight: "bold",
  alignSelf: "center",
  textTransform: "uppercase"
},
create:{
  width:80,
  height:100,
  backgroundColor:'#FF0000',
  color:'#000000',
  margin:10,
},
signIn: {
  width: '95%',
  height: 60,
  justifyContent: 'center',
  borderRadius: 5,
  backgroundColor:'#fff',
  padding:10,
},
textSign: {
  fontSize: 18,
  fontWeight: 'bold'
},

});

export default Home;
