import { PinchGestureHandler } from "react-native-gesture-handler";
import firebase from "./firebaseConfig";

export const AddUser = async(Username,email,phone,uid) =>{
    try {
        return await firebase.database().ref('user/' +uid).
        set({
            Username:Username,
            email:email,
            phone:phone
        })
    } catch (error) {
        return error;
    }
}
