import React from 'react';
import { StyleSheet, Text, View,StatusBar} from 'react-native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { NavigationContainer,DarkTheme } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import Icon from 'react-native-vector-icons/Ionicons';
import HomeScreen from './Screens/Home'
import SettingsScreen from './Screens/Settings'
import { color } from 'react-native/Libraries/Components/View/ReactNativeStyleAttributes';
import BottomBar from './Screens/BottomBar';
import { Animated, Dimensions, Image, Platform,  TouchableOpacity,  } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import MainTabScreen from './Screens/MainTabScreen';
import { DrawerContent  } from './Screens/DrawerContent';
import {Provider as PaperProvider,DarkTheme as PaperDarkTheme } from 'react-native-paper'
import RootStackScreen  from './Screens/RootStackScreen';
const Drawer = createDrawerNavigator();
import RootStack from './Screens/RootStackScreen';
import Location from './Screens/Location';
import FriendsList from './Screens/FriendsList';






export default function App() {
  const [isDarkTheme,setIsDarkTheme] = React.useState(false);
  return (

    <NavigationContainer  independent={true}>
        <StatusBar  backgroundColor="lightblue" barStyle="light-content"/>
        <Drawer.Navigator drawerContent={props => <DrawerContent {...props} />}>
        <Drawer.Screen name="RootStack" component={RootStack}/>
          <Drawer.Screen name="HomeDrawer" component={MainTabScreen} />

          <Drawer.Screen name="SettingsScreen" component={SettingsScreen} />

        </Drawer.Navigator>
        <View >
      <StatusBar
      barStyle="light-content"
      backgroundColor="lightblue"
      hidden={false}
      />
    </View>
    </NavigationContainer>

  );
  }
