import React,{useState} from 'react';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    StatusBar,
    Alert,
    ActivityIndicator,
    Dimensions
} from 'react-native';
import { SignUpUser } from '../Components/SignUp';
import * as Animatable from 'react-native-animatable';
import LinearGradient from 'react-native-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import firestore, { FirebaseFirestoreTypes } from '@react-native-firebase/firestore';
import { useTheme } from 'react-native-paper';
import { AddUser } from '../Components/Users';
import { AuthContext } from '../Components/context';
import Users from '../Model/users';
import firebase from '../Components/firebaseConfig';
const SignUpScreen = ({navigation}) => {
    
    state = {
      Username:"", 
      email: "" ,
      phone: "" ,
      password: ""

}
    
    
    

    const [data, setData] = React.useState({
        username: '',
        Email:'',
        Number:'',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidEmail:true,
        isValidNumber:true,
        isValidPassword: true,
    });

    const { colors } = useTheme();

 

    const textInputChange = (val) => {
        if( val.trim().length >= 4 ) {
            setData({
                ...data,
                
                check_textInputChange: true,
                isValidUser: true
            });
        } else {
            setData({
                ...data,
            
                check_textInputChange: false,
                isValidUser: false
            });
        }
    }
    const textInputChange1 = (val) => {
        if( val.trim().length >= 4 ) {
            setData({
                ...data,
                
                check_textInputChange: true,
                isValidEmail: true
            });
        } else {
            setData({
                ...data,
                
                check_textInputChange: false,
                isValidEmail: false
            });
        }
    }


    const textInputChange2 = (val) => {
        if( val.trim().length >= 10 ) {
            setData({
                ...data,
                
                check_textInputChange: true,
                isValidNumber: true
            });
        } else {
            setData({
                ...data,
                
                check_textInputChange: false,
                isValidNumber: false
            });
        }
    }

    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    }

    const handleValidUser = (val) => {
        if( val.trim().length >= 4 ) {
            setData({
                ...data,
           
                isValidUser: true
            });
        } else {
            setData({
                ...data,
                
                isValidUser: false
            });
        }
    }
    const handleValidEmail = (val) => {
        if( val.trim().length >= 4 ) {
            setData({
                ...data,
                isValidEmail: true
            });
        } else {
            setData({
                ...data,
              
                isValidEmail: false
            });
        }
    }
    const handleValidNumber = (val) => {
        if( val.trim().length >= 10 ) {
            setData({
                ...data,
               
                isValidNumber: true
            });
        } else {
            setData({
                ...data,
                
                isValidNumber: false
            });
        }
    }

 Signupforfirebase = () =>{
    SignUpUser(this.state.email, this.state.password).
    then((res)=>{
        console.log('res',res);
        var userUID= firebase.auth().currentUser.uid;
        console.log(userUID);
        AddUser(this.state.Username, this.state.email, this.state.phone, userUID).
        then(()=>{
            alert('success');
        })
    }).
    catch((err)=>{
        alert(err);
    })
 }
    return (
      <View style={styles.container}>
          <StatusBar backgroundColor='lightblue' barStyle="light-content"/>
          <View style={styles.header1}>
            <Animatable.Image 
               
            source={require('../assets/loca.webp')}
            style={styles.logo}
            resizeMode="stretch"
            />
            <Text style= {styles.container1}>FindMe</Text>
        </View>

        <Animatable.View 
            animation="fadeInUpBig"
            style={styles.footer}
        >
            <Text style={[styles.text_footer, {
                color: colors.text
            }]}>Username</Text>
            <View style={styles.action}>
            <FontAwesome 
                    name="user-o"
                    color={colors.text}
                    size={20}
                />
                <TextInput 
                    placeholder="Your Name"
                    placeholderTextColor="#666666"
                    style={[styles.textInput, {
                        color: colors.text
                    }]}
                    autoCapitalize="none"
                   
                    
                  
                    onEndEditing={(e)=>handleValidEmail(e.nativeEvent.text)}
                />
                {data.check_textInputChange1 ? 
                <Animatable.View
                    animation="bounceIn"
                >
                    <Feather 
                        name="check-circle"
                        color="green"
                        size={20}
                    />
                </Animatable.View>
                : null}
            </View>
            { data.isValidUser ? null : 
            <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.errorMsg}>Username must be 4 characters long.</Text>
            </Animatable.View>
            }


            <Text style={[styles.text_footer, {
                marginTop: 20,
                color: colors.text
            }]}>Email</Text>
            <View style={styles.action}>
                <FontAwesome 
                    name="user-o"
                    color={colors.text}
                    size={20}
                />
                <TextInput 
                    placeholder="Your Email"
                    placeholderTextColor="#666666"
                    style={[styles.textInput, {
                        color: colors.text
                    }]}
                    autoCapitalize="none"
                    
                   
                    updatefields={(text) =>this.setState({email:text})}
                    onEndEditing={(e)=>handleValidEmail(e.nativeEvent.text)}
                />
                {data.check_textInputChange1 ? 
                <Animatable.View
                    animation="bounceIn"
                >
                    <Feather 
                        name="check-circle"
                        color="green"
                        size={20}
                    />
                </Animatable.View>
                : null}
            </View>
            { data.isValidEmail ? null : 
            <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.errorMsg}>Email must be in formate user1@gmail.com</Text>
            </Animatable.View>
            }



            <Text style={[styles.text_footer, {
                marginTop: 20,
                color: colors.text
            }]}>Phone</Text>
            <View style={styles.action}>
            <Feather 
                    name="phone"
                    color={colors.text}
                    size={20}
                />
            <TextInput 
                    placeholder="Your Phone"
                    keyboardType='numeric'
                    placeholderTextColor="#666666"
                    style={[styles.textInput, {
                        color: colors.text
                    }]}
                    autoCapitalize="none"
                    
                    
                    updatefields={(text) =>this.setState({phone:text})}
                    onEndEditing={(e)=>handleValidEmail(e.nativeEvent.text)}
                />
                
                {data.check_textInputChange2 ? 
                <Animatable.View
                    animation="bounceIn"
                >
                    <Feather 
                        name="check-circle"
                        color="green"
                        size={20}
                    />
                </Animatable.View>
                : null}
            </View>
            { data.isValidNumber ? null : 
            <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.errorMsg}>Username must be 10 digits long.</Text>
            </Animatable.View>
            }
            

            <Text style={[styles.text_footer, {
                color: colors.text,
                marginTop: 20
            }]}>Password</Text>
            <View style={styles.action}>
                <Feather 
                    name="lock"
                    color={colors.text}
                    size={20}
                />
                <TextInput 
                    placeholder="Your Password"
                    placeholderTextColor="#666666"
                    secureTextEntry={data.secureTextEntry ? true : false}
                    style={[styles.textInput, {
                        color: colors.text
                    }]}
                    autoCapitalize="none"
                   
                    updatefields={(text) =>this.setState({password:text})}
                   
                />
                <TouchableOpacity
                    onPress={updateSecureTextEntry}
                >
                    {data.secureTextEntry ? 
                    <Feather 
                        name="eye-off"
                        color="grey"
                        size={20}
                    />
                    :
                    <Feather 
                        name="eye"
                        color="grey"
                        size={20}
                    />
                    }
                </TouchableOpacity>
            </View>
            { data.isValidPassword ? null : 
            <Animatable.View animation="fadeInLeft" duration={500}>
            <Text style={styles.errorMsg}>Password must be 8 characters long.</Text>
            </Animatable.View>
            }
            

            
            <View style={styles.button}>

            <TouchableOpacity
                    onPress={() => { this.Signupforfirebase() }}
                    style={[styles.signIn, {
                        borderColor: '#009387',
                        borderWidth: 1,
                        marginTop: 15
                    }]}
                >
                    <Text style={[styles.textSign, {
                        color: '#fff'
                    }]}>Sign Up</Text>
                </TouchableOpacity>
            </View>
        </Animatable.View>
      </View>
       );
    };
    
